UPDATE `emailtemplate` SET `Body`='<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\r                <html xmlns=\"http://www.w3.org/1999/xhtml\"> <head><meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" /><title>@Model.Base.CompanyName</title><style type=\"text/css\">body {\r                            padding-left: 10px !important;\r                            padding-right: 10px !important;\r                            padding-top: 10px !important;\r                            padding-bottom: 10px !important;\r                            margin: 20px !important;\r                            width: 600px;\r                            border: solid 1px #B2C8D9;\r                            -webkit-text-size-adjust: 100% !important;\r                            -ms-text-size-adjust: 100% !important;\r                            -webkit-font-smoothing: antialiased !important;\r                            font-size: 16px;\r                        }\r                        a {\r                            color: #382F2E;\r                        }\r                \r                        p, h1 {\r                            margin: 0;\r                        }\r                \r                        p {\r                            text-align: left;\r                            font-weight: normal;\r                            line-height: 19px;\r                        }\r                \r                        a.link1 {\r                            color: #382F2E;\r                        }\r                \r                        a.link2 {\r                            text-decoration: none;\r                            color: #ffffff;\r                        }\r                \r                        h2 {\r                            text-align: left;\r                            color: #222222;\r                            font-size: 19px;\r                            font-weight: normal;\r                        }\r                \r                        div, p, ul, h1 {\r                            margin: 0;\r                        }\r                \r                        .bgBody {\r                            background: #ffffff;\r                        }\r                \r                          table thead tr th {\r               border: 1px solid black;\r               padding: 5px;\r               margin: 2px;\r               text-align: left;\r           }\r   \r           table tbody tr td {\r               border: 1px solid black;\r               padding: 5px;\r               margin: 2px;\r               text-align: left;\r           }\r                    </style>\r                </head>\r                <body style=\"background-repeat: repeat; -webkit-text-size-adjust: 100%; -ms-text-size-adjust: 100%; -webkit-font-smoothing: antialiased;\">\r                        <div class=\"contentEditableContainer contentTextEditable\">\r                            <div class=\"contentEditable\">\r                                <span>Dear @Model.FullName,</span>\r                                <br />\r                                <br />\r                                \r                             <p>\r             This is to confirm your appointment with MARBLELIFE <sup>&reg;</sup> tomorrow at @Model.StartDate at @Model.Time with Representative @Model.techNames We look forward to seeing you and assisting you with your project. <br/>Have a great day!\r                           </p>\r                                   <br />\r                                    @foreach (var item in Model.TechList)\r                                {\r                            <p>\r                                @item.TechName <div class=\"margin-left:10px\">\r                                </div> <img src=\"@item.src\" style=\"height: 100px; width: 100px;display:@Model.display\" />\r                                <br/>\r                            </p>\r                            }\r            				<br />\r                         <table style=\"border: 1px solid black;border-collapse:collapse;\">\r                           <thead>\r                               <tr>\r                               <th style=\"border: 1px solid black;\">Technician Name</th>\r  								 <th style=\"border: 1px solid black;\">Start Date</th>\r  								 <th style=\"border: 1px solid black;\">End Date</th>\r                                   \r  								\r                               </tr>\r                           </thead>\r   						 @foreach(var item in Model.TechList) \r  						 { \r                           <tbody>\r                               <tr>\r                                   <td style=\"border: 1px solid black;\">@item.FirstName @item.LastName </td>\r                                   <td style=\"border: 1px solid black;\">@item.StartDate</td>\r  								 <td style=\"border: 1px solid black;\">@item.EndDate</td>\r                               </tr>\r                           </tbody>\r   						}\r                       </table> \r                       <br/>\r            				Regards,\r                               \r                                <p>\r                                  @Model.Base.CompanyName<sup>&reg;</sup> <br />\r                                  @Model.AssigneePhone <br />\r                				  </p>\r                                \r                            </div>\r                         </div>\r                </body>\r                </html>' WHERE `Id`='25';
