ALTER TABLE `jobestimateimagecategory` 
DROP FOREIGN KEY `fk_jobEstimateimageCategory_marketingclass`;
ALTER TABLE `jobestimateimagecategory` 
CHANGE COLUMN `MarkertingClassId` `MarkertingClassId` BIGINT(20) NULL DEFAULT NULL ;
ALTER TABLE `jobestimateimagecategory` 
ADD CONSTRAINT `fk_jobEstimateimageCategory_marketingclass`
  FOREIGN KEY (`MarkertingClassId`)
  REFERENCES `marketingclass` (`Id`)
  ON DELETE NO ACTION
  ON UPDATE NO ACTION;
  
  
  Create table BeforeAfter
SELECT jr.id, jr.jobid,js.servicetypeid,jr.FileId,jr.DataRecorderMetaDataId,d.DateCreated,d.DateModified,d.createdby,d.ModifiedBy,jr.estimateId,js.Id as SchedulerId FROM makalu.jobresource jr join makalu.jobscheduler js on jr.jobid = js.jobid
join DataRecorderMetaData d on d.id = jr.DataRecorderMetaDataId
and jr.isDeleted=b'0' and js.isDeleted=b'0'
Union 
SELECT jr.id, jr.jobId,js.servicetypeid,jr.FileId,jr.DataRecorderMetaDataId,d.DateCreated,d.DateModified,d.createdby,d.ModifiedBy,jr.estimateId,js.Id as SchedulerId FROM makalu.jobresource jr join makalu.jobscheduler js on jr.estimateid = js.estimateid
join DataRecorderMetaData d on d.id = jr.DataRecorderMetaDataId
and jr.isDeleted=b'0' and js.isDeleted=b'0';


UPDATE `emailtemplate` SET `Body`='<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n             <html xmlns=\"http://www.w3.org/1999/xhtml\">\n             <head>\n                 <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\n                 <title>@Model.Base.ApplicationName</title>\n                 <style type=\"text/css\">\n                     body {\n                         padding-left: 10px !important;\n                         padding-right: 10px !important;\n                         padding-top: 10px !important;\n                         padding-bottom: 10px !important;\n                         margin: 20px !important;\n                         width: 600px;\n                         border: solid 1px #B2C8D9;\n                         -webkit-text-size-adjust: 100% !important;\n                         -ms-text-size-adjust: 100% !important;\n                         -webkit-font-smoothing: antialiased !important;\n                         font-size: 16px;\n                     }\n                     a {\n                         color: #382F2E;\n                     }\n             \n                     p, h1 {\n                         margin: 0;\n                     }\n             \n                     p {\n                         text-align: left;\n                         font-weight: normal;\n                         line-height: 19px;\n                     }\n             \n                     a.link1 {\n                         color: #382F2E;\n                     }\n             \n                     a.link2 {\n                         text-decoration: none;\n                         color: #ffffff;\n                     }\n             \n                     h2 {\n                         text-align: left;\n                         color: #222222;\n                         font-size: 19px;\n                         font-weight: normal;\n                     }\n             \n                     div, p, ul, h1 {\n                         margin: 0;\n                     }\n             \n                     .bgBody {\n                         background: #ffffff;\n                     }\n             \n                     table thead tr th {\n                         border: 1px solid black;\n                         padding: 5px;\n                         margin: 2px;\n                         text-align: left;\n                     }\n             \n                     table tbody tr td {\n                         border: 1px solid black;\n                         padding: 5px;\n                         margin: 2px;\n                         text-align: left;\n                     }\n                 </style>\n             </head>\n             <body style=\"background-repeat: repeat; -webkit-text-size-adjust: 100%; -ms-text-size-adjust: 100%; -webkit-font-smoothing: antialiased;\">\n                     <div class=\"contentEditableContainer contentTextEditable\">\n                         <div class=\"contentEditable\">\n                             <span>Dear @Model.CustomerName,</span>\n                             <br>\n                             <br />\n                             <p>\n            				\n             				Thank you once again for the opportunity to assist you in your efforts to enhance your building. \n  \n  Attached you will find A BEFORE-and-AFTER of your project for your records...and to share with friends and family. \n  \n  We look forward to working with you again on your next project. \n  \n  \n                            <br />\n            				<br/>\n                                 Regards,\n                                 <br />\n             					<p>                    \n             					@Model.Base.CompanyName <br>\n             					</p>\n                             </p>\n                         </div>\n                     </div>\n             </body>\n             </html>' WHERE `Id`='34';


