UPDATE `makalu`.`emailtemplate` SET `Body`='<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n                 <html xmlns=\"http://www.w3.org/1999/xhtml\">\n                 <head>\n                     <meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\n                     <title>@Model.Base.ApplicationName</title>\n                     <style type=\"text/css\">\n                         body {\n                             padding-left: 10px !important;\n                             padding-right: 10px !important;\n                             padding-top: 10px !important;\n                             padding-bottom: 10px !important;\n                             margin: 20px !important;\n                             width: 600px;\n                             border: solid 1px #B2C8D9;\n                             -webkit-text-size-adjust: 100% !important;\n                             -ms-text-size-adjust: 100% !important;\n                             -webkit-font-smoothing: antialiased !important;\n                             font-size: 16px;\n                         }\n                         a {\n                             color: #382F2E;\n                         }\n                 \n                         p, h1 {\n                             margin: 0;\n                         }\n                 \n                         p {\n                             text-align: left;\n                             font-weight: normal;\n                             line-height: 19px;\n                         }\n                 \n                         a.link1 {\n                             color: #382F2E;\n                         }\n                 \n                         a.link2 {\n                             text-decoration: none;\n                             color: #ffffff;\n                         }\n                 \n                         h2 {\n                             text-align: left;\n                             color: #222222;\n                             font-size: 19px;\n                             font-weight: normal;\n                         }\n                 \n                         div, p, ul, h1 {\n                             margin: 0;\n                         }\n                 \n                         .bgBody {\n                             background: #ffffff;\n                         }\n                 \n                         table thead tr th {\n                             border: 1px solid black;\n                             padding: 5px;\n                             margin: 2px;\n                             text-align: left;\n                         }\n                 \n                         table tbody tr td {\n                             border: 1px solid black;\n                             padding: 5px;\n                             margin: 2px;\n                             text-align: left;\n                         }\n                     </style>\n                 </head>\n                 <body style=\"background-repeat: repeat; -webkit-text-size-adjust: 100%; -ms-text-size-adjust: 100%; -webkit-font-smoothing: antialiased;\">\n                         <div class=\"contentEditableContainer contentTextEditable\">\n                             <div class=\"contentEditable\">\n                                 <span>Hi,</span>\n                                 <br />\n                                 <p>\n                				\n                 				The loan with Id @Model.LoanId  has just received final payment from FRANCHISE.\n                              <br/>							\n                              Please take a moment to: \n  							<br/>\n  							<ol>\n   <li>Complete final review and reconciliation of loan between MARBLELIFE INFORMATION SYSTEM and your ACCOUNTING to confirm all is in order.</li>\n   <li>If in order – please email the franchise to let them know their loan has been satisfied and is now deemed to be fully paid. </li>\n   <li>If not in order – please assess reason and either correct or convey to programmer for adjustment in the system.</li>\n </ol> \n      \n                              <br />\n <i>This is an automated email from the system issued when the system believes a loan for a franchise has been completed.</i>\n                				<br/><br/>\n                                     Regards,\n                                     <br />\n                 					<p>                    \n                 					@Model.Base.CompanyName <br>\n                 					</p>\n                                 </p>\n                             </div>\n                         </div>\n                 </body>\n                 </html>' WHERE `Id`='36';




UPDATE `emailtemplate` SET `Subject`='@Model.Franchisee Loan  has been Completed ' WHERE `Id`='36';
