UPDATE `makalu`.`emailtemplate` SET `Body` = '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n   <html xmlns=\"http://www.w3.org/1999/xhtml\">\n   	<head>\n   		<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\n   		<title>@Model.Base.CompanyName</title>\n   		<style type=\"text/css\">\n   			body {\n                   padding-left: 10px !important;\n                   padding-right: 10px !important;\n                   padding-top: 10px !important;\n                   padding-bottom: 10px !important;\n                   margin: 20px !important;\n                   width: 600px;\n                   border: solid 1px #B2C8D9;\n                   -webkit-text-size-adjust: 100% !important;\n                   -ms-text-size-adjust: 100% !important;\n                   -webkit-font-smoothing: antialiased !important;\n                   font-size: 16px;\n               }\n               a {\n                   color: #382F2E;\n               }\n               p, h1 {\n                   margin: 0;\n               }\n               p {\n                   text-align: left;\n                   font-weight: normal;\n                   line-height: 19px;\n               }\n               a.link1 {\n                   color: #382F2E;\n               }\n               a.link2 {\n                   text-decoration: none;\n                   color: #ffffff;\n               }\n               h2 {\n                   text-align: left;\n                   color: #222222;\n                   font-size: 19px;\n                   font-weight: normal;\n               }\n               div, p, ul, h1 {\n                   margin: 0;\n               }\n               .bgBody {\n   				background: #ffffff;\n   			}\n               table thead tr th {\n                   border: 1px solid black;\n                   padding: 5px;\n                   margin: 2px;\n                   text-align: left;\n               }\n               table tbody tr td {\n                   border: 1px solid black;\n                   padding: 5px;\n                   margin: 2px;\n                   text-align: left;\n               }\n               .block\n               {\n                   display:block;\n               }\n               .none\n               {\n               display:none;\n               }\n               .btn-confirm\n               {\n   				background-color:#26327e !important;\n   				margin-left:30% !important;\n   				color: white!important;\n   				border:none !important;\n   				width:40% !important;\n   				margin-top:5% !important;\n   				height:42px !important;\n               }\n               .btn-cancel\n               {\n                   background-color:#26327e !important;\n                   margin-left:30% !important;\n                   color: white!important;\n                   border:none !important;\n                   width:38% !important;\n                   margin-top:5% !important;\n                   height:42px !important;\n   			}\n           </style>\n       </head>\n       <body style=\"background-repeat: repeat; -webkit-text-size-adjust: 100%; -ms-text-size-adjust: 100%; -webkit-font-smoothing: antialiased;\">\n   		<div class=\"contentEditableContainer contentTextEditable\">\n   			<div class=\"contentEditable\">\n                   <span>Dear @Model.CustomerName,</span>\n                   <br /><br />   \n                   <p>\n                   Once again, THANK YOU, for the opportunity to assist you with the restoration-and-maintenance of your building surfaces.\n   				<br/><br/>\n   				@Model.InvoicesName\n   				<br/><br/> \n                <div class =\"@Model.AllInvoicesNotSigned\">\n   				Assuming this meets your approval, we can consider that our Technician can start his Job for the Invoice(s) signed.\n   				</div>\n                <br/><br/>\n                     <div class=\"@Model.AllInvoicesSigned\">\n   						Please review the Invoice attached in the mail.\n   					</div>\n  					<div class =\"@Model.AllInvoicesNotSigned\">\n   						Please review the Invoice attached in the mail and <a href=@Model.Url>Click here</a> to sign the unsigned Invoices if you change your mind in the future. Please use 4 digit code @Model.Code to verify your account.\n   					</div>\n   					<br/>\n   					@Model.InvoicesSignedBy\n            		<br/><br/>\n   				We look forward to working with you.							\n                   </p>\n                   <br /><br/><br/>\n   				Regards,\n                   <p>\n                     @Model.Base.CompanyName<sup>&reg;</sup> <br />\n                     @Model.AssigneePhone <br />\n                   </p>\n   			</div>\n           </div>\n       </body>\n   </html>' WHERE (`Id` = '571');

UPDATE `makalu`.`emailtemplate` SET `Body` = '<!DOCTYPE html PUBLIC \"-//W3C//DTD XHTML 1.0 Transitional//EN\" \"http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd\">\n   	<html xmlns=\"http://www.w3.org/1999/xhtml\">\n   	<head>\n   		<meta http-equiv=\"Content-Type\" content=\"text/html; charset=utf-8\" />\n   		<title>@Model.Base.CompanyName</title>\n   		<style type=\"text/css\">\n   			body{\n   				padding-left: 10px !important;\n   				padding-right: 10px !important;\n   				padding-top: 10px !important;\n   				padding-bottom: 10px !important;\n   				margin: 20px !important;\n   				width: 600px;\n   				border: solid 1px #B2C8D9;\n   				-webkit-text-size-adjust: 100% !important;\n   				-ms-text-size-adjust: 100% !important;\n   				-webkit-font-smoothing: antialiased !important;\n   				font-size: 16px;\n   			}\n   			a {\n   				color: #382F2E;\n   			}\n   			p, h1 {\n   				margin: 0;\n   			}\n   			p {\n   				text-align: left;\n   				font-weight: normal;\n   				line-height: 19px;\n   			}\n   			a.link1 {\n   				color: #382F2E;\n   			}\n   			a.link2 {\n   				text-decoration: none;\n   				color: #ffffff;\n   			}\n   			h2 {\n   				text-align: left;\n   				color: #222222;\n   				font-size: 19px;\n   				font-weight: normal;\n   			}\n   			div, p, ul, h1 {\n   				margin: 0;\n   			}\n   			.bgBody {\n   				background: #ffffff;\n   			}\n   			table thead tr th {\n   				border: 1px solid black;\n   				padding: 5px;\n   				margin: 2px;\n   				text-align: left;\n   			}\n   			table tbody tr td {\n   				border: 1px solid black;\n   				padding: 5px;\n   				margin: 2px;\n   				text-align: left;\n   			}\n   			.block{\n   				display:block;\n   			}\n   			.none{\n   				display:none;\n   			}\n   			.btn-confirm\n   			{\n   				background-color:#26327e !important;\n   				margin-left:30% !important;\n   				color: white!important;\n   				border:none !important;\n   				width:40% !important;\n   				margin-top:5% !important;\n   				height:42px !important;\n   			}\n   			.btn-cancel\n   			{\n   				background-color:#26327e !important;\n   				margin-left:30% !important;\n   				color: white!important;\n   				border:none !important;\n   				width:38% !important;\n   				margin-top:5% !important;\n   				height:42px !important;\n   			}\n   		</style>\n       </head>\n       <body style=\"background-repeat: repeat; -webkit-text-size-adjust: 100%; -ms-text-size-adjust: 100%; -webkit-font-smoothing: antialiased;\">\n           <div class=\"contentEditableContainer contentTextEditable\">\n               <div class=\"contentEditable\">\n                   <span>Dear Admin</span>\n                   <br /><br />\n                   <p>\n   					Technician has marked the Invoice upon completion for JOB <a href=\"@Model.SchedulerUrl\">@Model.JobId</a> as customer not available, indicating the customer is not available for signature upon completion of the job. \n   					<br/><br/>\n   					Your MARBLELIFE proposal is attached for your review and confirmation of the Job completion.\n   					<br/><br/> \n   					A mail has been triggered automatically to the customer to sign the Invoices for approving the Job completion.\n   					<br/><br/>\n  	\n                   </p>\n   				<br /><br/><br/>Regards,\n                   <p>\n                       @Model.Base.CompanyName<sup>&reg;</sup> <br />\n                       @Model.AssigneePhone <br />\n                   </p>\n               </div>\n           </div>\n       </bo\n   </html>' WHERE (`Id` = '576');
